import React, { useState } from "react";
import API from "../api";
import { useNavigate } from "react-router-dom";

const AddBookPage = () => {
    const [formData, setFormData] = useState({
        name: "",
        author: "",
        price: "",
        rackNo: "",
        edition: "",
        dateOfPurchase: "",
    });

    const navigate = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await API.post("/books", formData);
            alert("Book added successfully!");
            navigate("/"); // Redirect to the books list page
        } catch (error) {
            console.error("Error adding book:", error);
            alert("Failed to add book. Please try again.");
        }
    };

    return (
        <div className="container mt-5">
            <h2>Add a New Book</h2>
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label htmlFor="name" className="form-label">Book Name</label>
                    <input
                        type="text"
                        className="form-control"
                        id="name"
                        name="name"
                        value={formData.name}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="mb-3">
                    <label htmlFor="author" className="form-label">Author</label>
                    <input
                        type="text"
                        className="form-control"
                        id="author"
                        name="author"
                        value={formData.author}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="mb-3">
                    <label htmlFor="price" className="form-label">Price</label>
                    <input
                        type="number"
                        className="form-control"
                        id="price"
                        name="price"
                        value={formData.price}
                        onChange={handleChange}
                    />
                </div>
                <div className="mb-3">
                    <label htmlFor="rackNo" className="form-label">Rack Number</label>
                    <input
                        type="text"
                        className="form-control"
                        id="rackNo"
                        name="rackNo"
                        value={formData.rackNo}
                        onChange={handleChange}
                    />
                </div>
                <div className="mb-3">
                    <label htmlFor="edition" className="form-label">Edition</label>
                    <input
                        type="text"
                        className="form-control"
                        id="edition"
                        name="edition"
                        value={formData.edition}
                        onChange={handleChange}
                    />
                </div>
                <div className="mb-3">
                    <label htmlFor="dateOfPurchase" className="form-label">Date of Purchase</label>
                    <input
                        type="date"
                        className="form-control"
                        id="dateOfPurchase"
                        name="dateOfPurchase"
                        value={formData.dateOfPurchase}
                        onChange={handleChange}
                    />
                </div>
                <button type="submit" className="btn btn-primary">Add Book</button>
            </form>
        </div>
    );
};

export default AddBookPage;
